package Practice;

public class ElseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int time=22;
		if(time<12)
			System.out.println("Good morning");
		else if(time<17)
			System.out.println("Good evening");
		else
			System.out.println("Good night");
		

	}

}
